package Calculadora;

public class Calculadora {
	public int soma(int i, int j){
		 return i+j;
	}

	public int subtrai(int i, int j) {
		// TODO Auto-generated method stub
		return i - j;
	}

	public int divide(int i, int j) {
		// TODO Auto-generated method stub
		return i/j;
	}

	public int multiplica(int i, int j) {
		// TODO Auto-generated method stub
		return i * j;
	}
}
